import React, { useEffect, useState } from "react";
import "./App.css"; // Ensure your CSS file is updated accordingly

// Define all possible card images
const cardImages = [
  { src: "/img/helmet-1.png" },
  { src: "/img/potion-1.png" },
  { src: "/img/ring-1.png" },
  { src: "/img/scroll-1.png" },
  { src: "/img/shield-1.png" },
  { src: "/img/sword-1.png" },
];

// Define the specific pairs that can be chosen
const specificPairs = [
  ["/img/helmet-1.png", "/img/potion-1.png"],
  ["/img/ring-1.png", "/img/scroll-1.png"],
  ["/img/shield-1.png", "/img/sword-1.png"],
];

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [score, setScore] = useState(0); // Score state
  const [chances, setChances] = useState(3); // User has 3 chances
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);
  const [disabled, setDisabled] = useState(false);

  // Shuffle and generate cards
  const shuffleCards = () => {
    // Randomly choose a specific pair
    const randomPair = specificPairs[Math.floor(Math.random() * specificPairs.length)];
    
    // Create a shuffled deck with the chosen specific pair
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: Math.random() }))
      .filter(card => randomPair.includes(card.src));
      
    // Ensure there are exactly two of each card
    const finalCards = [
      ...shuffledCards.filter(card => card.src === randomPair[0]),
      ...shuffledCards.filter(card => card.src === randomPair[1]),
    ].sort(() => Math.random() - 0.5);

    setCards(finalCards);
    setTurns(0);
    setScore(0);  // Reset score
    setChances(3);  // Reset chances to 3
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  // Handle a card choice
  const handleChoice = (card) => {
    choiceOne ? setChoiceTwo(card) : setChoiceOne(card);
  };

  // Compare two selected cards
  useEffect(() => {
    if (choiceOne && choiceTwo) {
      setDisabled(true);
      if (choiceOne.src === choiceTwo.src) {
        setCards((prevCards) => {
          return prevCards.map((card) => {
            if (card.src === choiceOne.src) {
              return { ...card, matched: true };
            } else {
              return card;
            }
          });
        });

        // Increase score only if the matched cards are the specific pair
        if ((choiceOne.src === specificPairs[0][0] && choiceTwo.src === specificPairs[0][1]) ||
            (choiceOne.src === specificPairs[0][1] && choiceTwo.src === specificPairs[0][0])) {
          setScore((prevScore) => prevScore + 1);  // Increase score by 1
        }
        
        resetTurn();
      } else {
        setChances((prevChances) => prevChances - 1);  // Decrease chances by 1
        setTimeout(() => resetTurn(), 1000);
      }
    }
  }, [choiceOne, choiceTwo]);

  // Reset choices and increase turn count
  const resetTurn = () => {
    setChoiceOne(null);
    setChoiceTwo(null);
    setTurns((prevTurns) => prevTurns + 1);
    setDisabled(false);
  };

  // End the game if chances reach 0
  useEffect(() => {
    if (chances === 0) {
      alert(`Game Over! Your score is ${score}. Try again.`);
      shuffleCards();
    }
  }, [chances, score]);

  // Start a new game automatically
  useEffect(() => {
    shuffleCards();
  }, []);

  return (
    <div className="App">
      <h1>Memory Card Game</h1>
      <button onClick={shuffleCards}>New Game</button>
      <div className="stats">
        <p>Turns: {turns}</p>
        <p>Score: {score}</p>
        <p>Chances Left: {chances}</p>
      </div>
      <div className="card-grid">
        {cards.map((card) => (
          <SingleCard
            key={card.id}
            card={card}
            handleChoice={handleChoice}
            flipped={card === choiceOne || card === choiceTwo || card.matched}
            disabled={disabled}
          />
        ))}
      </div>
    </div>
  );
}

function SingleCard({ card, handleChoice, flipped, disabled }) {
  const handleClick = () => {
    if (!disabled) {
      handleChoice(card);
    }
  };

  return (
    <div className={`card ${flipped ? "flipped" : ""}`}>
      <div className="card-inner">
        {/* Front of the card */}
        <img className="front" src={card.src} alt="card front" />
        {/* Back of the card */}
        <img
          className="back"
          src="/img/cover.png"
          onClick={handleClick}
          alt="card back"
        />
      </div>
    </div>
  );
}

export default App;
