import React, { useEffect, useState } from "react";
import "./App.css"; // Ensure your CSS file is updated accordingly

// Define card images with specific pairs
const cardImages = [
  { src: "/img/helmet-1.png", id: 1 },
  { src: "/img/potion-1.png", id: 2 },
  { src: "/img/ring-1.png", id: 3 },
  { src: "/img/scroll-1.png", id: 4 },
  { src: "/img/shield-1.png", id: 5 },
  { src: "/img/sword-1.png", id: 6 },
];

// Define the pairs that can be chosen (only these will affect the score)
const specificPairs = [
  [1, 2], // Helmet and Potion
  [3, 4], // Ring and Scroll
  [5, 6], // Shield and Sword
];

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);
  const [score, setScore] = useState(0);
  const [chances, setChances] = useState(10);
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);
  const [disabled, setDisabled] = useState(false);

  // Shuffle cards
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({ ...card, id: Math.random() }));
    setCards(shuffledCards);
    setTurns(0);
    setScore(0);
    setChances(10);
    setChoiceOne(null);
    setChoiceTwo(null);
  };

  // Handle card choice
  const handleChoice = (card) => {
    choiceOne ? setChoiceTwo(card) : setChoiceOne(card);
  };

  // Compare two selected cards
  useEffect(() => {
    if (choiceOne && choiceTwo) {
      setDisabled(true);
      if (choiceOne.src === choiceTwo.src) {
        setCards((prevCards) => {
          return prevCards.map((card) => {
            if (card.src === choiceOne.src) {
              return { ...card, matched: true };
            } else {
              return card;
            }
          });
        });

        // Check if the matched cards are part of the specific pairs
        const pairIdOne = cardImages.find(card => card.src === choiceOne.src).id;
        const pairIdTwo = cardImages.find(card => card.src === choiceTwo.src).id;

        if (specificPairs.some(pair => pair.includes(pairIdOne) && pair.includes(pairIdTwo))) {
          setScore((prevScore) => prevScore + 1);  // Increase score by 1
        }
        resetTurn();
      } else {
        setChances((prevChances) => prevChances - 1);  // Decrease chances by 1
        setTimeout(() => resetTurn(), 1000);
      }
    }
  }, [choiceOne, choiceTwo]);

  // Reset choices and increase turn count
  const resetTurn = () => {
    setChoiceOne(null);
    setChoiceTwo(null);
    setTurns((prevTurns) => prevTurns + 1);
    setDisabled(false);
  };

  // End the game if chances reach 0
  useEffect(() => {
    if (chances === 0) {
      alert(`Game Over! Your score is ${score}. Try again.`);
      shuffleCards();
    }
  }, [chances, score]);

  // Start a new game automatically
  useEffect(() => {
    shuffleCards();
  }, []);

  return (
    <div className="App">
      <h1>Memory Card Game</h1>
      <button onClick={shuffleCards}>New Game</button>
      <div className="stats">
        <p>Turns: {turns}</p>
        <p>Score: {score}</p>
        <p>Chances Left: {chances}</p>
      </div>
      <div className="card-grid">
        {cards.map((card) => (
          <SingleCard
            key={card.id}
            card={card}
            handleChoice={handleChoice}
            flipped={card === choiceOne || card === choiceTwo || card.matched}
            disabled={disabled}
          />
        ))}
      </div>
    </div>
  );
}

function SingleCard({ card, handleChoice, flipped, disabled }) {
  const handleClick = () => {
    if (!disabled) {
      handleChoice(card);
    }
  };

  return (
    <div className="card">
      <div className={flipped ? "flipped" : ""}>
        {/* Front of the card */}
        <img className="front" src={card.src} alt="card front" />
        {/* Back of the card */}
        <img
          className="back"
          src="/img/cover.png"
          onClick={handleClick}
          alt="card back"
        />
      </div>
    </div>
  );
}

export default App;
